/* eslint-disable no-unused-vars */
import React, {useState, useContext, useEffect} from 'react';
import Layout from './Layout';
import Spotify from 'spotify-web-api-js';
import { ContextUser } from '../components/UserContext';

const spotifyWebApi = new Spotify();

const  TopList = () => {
  const {user, setUser} = useContext(ContextUser)
  const [topTracks, setTopTracks] = useState([])

  function getTopList() {
    spotifyWebApi.setAccessToken(user.token)
    spotifyWebApi.getMyTopTracks(user.token)
      .then(res => setTopTracks(Object.values(res)[0]))
    }
  console.log(topTracks);
  return (
    <Layout>
    <div className="TopList">
    <h1 className="TopList__title">Your Top Tracks</h1>
    {!topTracks.length && <button onClick={getTopList}>Get Top Tracks</button>}
    <ul className="TopList__list">
      {topTracks && topTracks.map((item, index) => (
      <>
      <article key={item.name} className="TopList__song--wrapper">
      <div className="TopList__content">
        <li key={item.name} className="TopList__song">{`${index + 1} - ${item.artists[0].name} - ${item.name}`}</li>
        <img className="TopList__image" src={item.album.images[1].url}></img>
        </div>
      </article>
      <audio key={`playback ${item.name}`} controls src={item.preview_url} />
      </>)
      )}
    </ul>
    </div>
    </Layout>
  );
}

export default TopList;
