import React, { useContext, useEffect } from 'react'
import Layout from './Layout';
import { ContextUser } from '../components/UserContext';

const App = () => {
  const [user, setUser] = useContext(ContextUser)
  useEffect(() => {
    const token = qs.parse(window.location.search);
    if (token.access_token) {
      setUser({token: token.access_token})
      axios.get('https://api.spotify.com/v1/me', {
        headers: {'Authorization': 'Bearer ' + token.access_token}
        }).then(res => console.log(res.data))
      .then(res => setUser({...user, name: res.data.display_name}))
    }
  }, [])
  return (
  <Layout>
  <div>
  <a href="http://localhost:8888/login"><button className="Auth__button">Log In</button></a>
      Welcome to my app, {user && user.name}
    </div>
  </Layout>
  )
}

export default App;
