import React, {useState, useEffect} from 'react';
import Spotify from 'spotify-web-api-js';

const spotifyWebApi = new Spotify();

const  App = () => {
  const [params, setParams] = useState(getHashParams())
  const [nowPlaying, setNowPlaying] = useState({name: 'none', image: ''})
  const [loggedIn, setLogin] = useState(params.access_token ? true : false)

  useEffect(() => {
    if (params.access_token) spotifyWebApi.setAccessToken(params.accesstoken)
  }, [loggedIn])

  function getHashParams() {
    var hashParams = {};
    var e, r = /([^&;=]+)=?([^&;]*)/g,
        q = window.location.hash.substring(1);
    while ( e = r.exec(q)) {
       hashParams[e[1]] = decodeURIComponent(e[2]);
    }
    setParams(hashParams)
  }

  function getNowPlaying() {
    spotifyWebApi.getMyCurrentPlaybackState()
      .then(res => setNowPlaying({name: res.item, image: res.item.album.images[0]}))
  }
  return (
    <div className="App">
    <div>Now Playing: {nowPlaying.name}</div>
    <img src={nowPlaying.image}></img>
    <a href="http://localhost:8888">
      <button>Log In</button>
      </a>
      <button onClick={getNowPlaying}>Check PLaying</button>
    </div>
  );
}

export default App;
