const  App = () => {
  const [tokens, getTokens] = useState(getHashParams())
  const [nowPlaying, getPlaying] = useState()
  function getHashParams() {
    var hashParams = {};
    var e, r = /([^&;=]+)=?([^&;]*)/g,
        q = window.location.hash.substring(1);
    while ( e = r.exec(q)) {
       hashParams[e[1]] = decodeURIComponent(e[2]);
    }
    return hashParams;
  }
  return (
    <div className="App">
    <div>Now Playing</div>
    <a href="http://localhost:8888">
      <button>Log In</button>
      </a>
    </div>
  );
}

export default App;
