/* eslint-disable no-unused-vars */
import React, {useState, useContext, useEffect} from 'react';
import Spotify from 'spotify-web-api-js';
import { ContextUser } from '../components/UserContext';

const spotifyWebApi = new Spotify();

const  TopList = () => {
  const {user, setUser} = useContext(ContextUser);
  const [topTracks, setTopTracks] = useState([]);
  const [isPlaying, setPlaying] = useState([]);

  function getTopList(period) {
    spotifyWebApi.setAccessToken(user.token)
    spotifyWebApi.getMyTopTracks(user.token)
      .then(res => setTopTracks(Object.values(res)[0]))
    }

  function playSong(e) {
    console.log(isPlaying)
    console.log(topTracks)
    const audio = e.target.nextSibling.nextSibling;
    audio.volume = 0.1;
    if (audio.paused) {
      audio.play()
      setPlaying(audio)
    } else {
      audio.pause();
      setPlaying([]);
    }
  }

  function addToPlayList() {
    const tracks = topTracks.map(track => track.uri)
    console.log(tracks);
    spotifyWebApi.createPlaylist(user.id, {
      name: 'Your Top Tracks - 30 days',
      public: true,
      collaborative: false,
      description: 'Your most listened to songs of the last 30 days.'
    }, (err, res) => {
      spotifyWebApi.addTracksToPlaylist(res.id, tracks)
    })
  }

  return (
    <div className="TopList">
    <h1 className="TopList__title">Your Top Tracks</h1>

    {!user.token && <p>Please log in to use this functionality.</p>}

    {user.token && !topTracks.length && <button onClick={() => getTopList(30)}>30 days</button>}

    {topTracks.length > 0 && <button onClick={addToPlayList}>Create playlist</button>}
    <ul className="TopList__list">
      {topTracks && topTracks.map((item, index) => (
      <article key={item.name} className="TopList__song--wrapper">
      <div className="TopList__playpause--wrapper">
          <div className={`playpause ${item.name}`}>
            <input onClick={e => playSong(e)} type="checkbox" defaultChecked={true} value="None" id={`playpause ${item.name}`} name="check" />
            <label htmlFor={`playpause ${item.name}`} tabIndex="1"></label>
            <audio loop volume="0.1" className={`audio_${item.name}`} key={`playback ${item.name}`} src={item.preview_url} />
          </div>
        </div>
      <div className="TopList__content">
        <li key={item.name} className="TopList__song">{`${index + 1} - ${item.artists[0].name} - ${item.name}`}</li>
        </div>
        <img className="TopList__image" src={item.album.images[1].url}></img>
      </article>
      ))}
    </ul>
    </div>
  );
}

export default TopList;
