/* eslint-disable no-unused-vars */
import React, {useState, useContext} from 'react';
import Layout from './Layout';
import Spotify from 'spotify-web-api-js';
import { ContextUser } from '../components/UserContext';

const spotifyWebApi = new Spotify();

const  Current = () => {
  const [user, setUser] = useContext(ContextUser)
  const [nowPlaying, setNowPlaying] = useState({name: 'none', image: ''})

  if (user.token) {
    spotifyWebApi.setAccessToken(user.token)
  }

  async function getNowPlaying () {
    const playback = await spotifyWebApi.getMyCurrentPlaybackState(user.token)
    if (playback) {
      console.log(playback);
    }
      // .then(res => setNowPlaying({name: res.item.name, image: res.item.album.images[0].url}))
  }
  return (
    <Layout>
    <div className="Current">
    <h2 className="Current_name">Now Playing: {nowPlaying.name}</h2>
    <img className="Current_img" alt="album-art" src={nowPlaying.image}></img>
      <button onClick={getNowPlaying}>Check PLaying</button>
    </div>
    </Layout>
  );
}

export default Current;
