/* eslint-disable no-unused-vars */
import React, {useState, useContext, useEffect} from 'react';
import Spotify from 'spotify-web-api-js';
import { ContextUser } from '../components/UserContext';

const spotifyWebApi = new Spotify();


const  Auth = () => {
  const [user, setUser] = useContext(ContextUser)
  const [params, setParams] = useState(getHashParams())
  console.log(params);

  if (params.access_token) {
    spotifyWebApi.setAccessToken(params.access_token)
  }
  useEffect(() => {
    setTimeout(() => {
      setParams(getHashParams())
      console.log(params);
    }, 1000);
  }, [])

  function getHashParams() {
    var hashParams = {};
    var e, r = /([^&;=]+)=?([^&;]*)/g,
        q = window.location.hash.substring(1);
    // eslint-disable-next-line no-cond-assign
    while ( e = r.exec(q)) {
       hashParams[e[1]] = decodeURIComponent(e[2]);
    }
    return hashParams;
  };

  const getUser = async () => {
    const res = await spotifyWebApi.getUser(params.access_token);
    console.log(res);
  }

  return (
    <div className="Auth">
    <a href="http://localhost:8888/login"><button className="Auth__button">Log In</button></a>
    <button onClick={getUser}>get user</button>
    </div>
  );
}

export default Auth;
