import React, { useContext} from 'react'
import { ContextUser } from '../components/UserContext';
import axios from 'axios';

const App = ({ logOutUser }) => {
  const {user, setUser} = useContext(ContextUser)

  const refreshToken = () => {
    axios.get(`/refresh_token?refresh_token:${user.refresh_token}`)
  }

  const renderPage = () => {
    if (user.access_token) {
      return (
        <>
        <h1 className="App__welcome">Welcome, {user && user.name}.</h1>
        <button className="App__logout" onClick={logOutUser}>Log Out</button>
        <button onClick={refreshToken}>Refresh Token Test</button>
        </>
      )
    } else {
      return (
        <section className="App">
        <h2 className="App__prompt">Welcome! Please log in to your Spotify Account.</h2>
        <a className="App__anchor" href="http://localhost:8888/login"><button className="Auth__button">Log In</button></a>
        </section>
      )
    }
  }

  return (
    <div>
      <>
      {renderPage()}
      </>
    </div>
  )
}

export default App;
