import React, { useState, useMemo, useEffect, useContext } from 'react';
import { BrowserRouter as Router, Route, Switch} from 'react-router-dom';
import App from '../components/App';
import About from '../components/About';
import Current from '../components/Current';
import TopList from '../components/TopList';
import { ContextUser } from '../components/UserContext';
import qs from 'query-string';
import axios from 'axios';

const AppRouter = () => {
  const [user, setUser] = useState({});
  const value = useMemo(() => ({ user, setUser }, [user, setUser]));

  useEffect(() => {
    const token = qs.parse(window.location.search);
    if (token.access_token) {
      axios.get('https://api.spotify.com/v1/me', {
        headers: {'Authorization': 'Bearer ' + token.access_token}
        }).then(res => setUser({token: token.access_token, name: res.data.display_name}))
    }
  }, [])

  useEffect(() => {
    const json = localStorage.getItem('userToken');
    const userToken = json;
    if (userToken) {
      setUser({json})
    }
  }, [])

  useEffect(() => {
    const json = user;
    localStorage.setItem('userToken', json)
  }, [user])

	return (
		<ContextUser.Provider value={value}>
			<Router>
				<>
					<Switch>
						<Route path='/' exact component={App} />
            <Route path='/callback' exact component={App} />
						<Route path='/about' component={About} />
						<Route path='/current' component={Current} />
            <Route path='/toplist' component={TopList} />
					</Switch>
				</>
			</Router>
		</ContextUser.Provider>
	);
};

export default AppRouter;
