import React, { useState, useMemo, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import App from '../components/App';
import About from '../components/About';
import Auth from '../components/Auth';
import Current from '../components/Current';
import { ContextUser } from '../components/UserContext';
import qs from 'query-string';
import axios from 'axios'

const AppRouter = () => {
	const [user, setUser] = useState({});
  const value = useMemo(() => ({ user, setUser }, [user, setUser]));

  useEffect(() => {
    const token = qs.parse(window.location.search);
    if (token.access_token) {
      setUser({token: token.access_token})
    }
    if (user.token) {
      axios.get('https://api.spotify.com/v1/me', {
        headers: {'Authorization': 'Bearer ' + user.token}
        }).then(res => console.log(res.data))
      .then(res => setUser({...user, name: res.data.display_name}))
    }
  }, [])

	return (
		<ContextUser.Provider value={value}>
			<Router>
				<div>
					<Switch>
						<Route path='/' exact component={App} />
            <Route path='/callback' exact component={Auth} />
						<Route path='/about' component={About} />
						<Route path='/current' component={Current} />
						<Route path='/auth' component={Auth} />
					</Switch>
				</div>
			</Router>
		</ContextUser.Provider>
	);
};

export default AppRouter;
