import React, { useContext, useEffect } from 'react'
import Layout from './Layout';
import { ContextUser } from '../components/UserContext';
import qs from 'query-string';
import axios from 'axios';

const App = () => {
  const [user, setUser] = useContext(ContextUser)
  useEffect(() => {
    const token = qs.parse(window.location.search);
    if (token.access_token) {
      axios.get('https://api.spotify.com/v1/me', {
        headers: {'Authorization': 'Bearer ' + token.access_token}
        }).then(res => setUser({token: token.access_token, name: res.data.display_name}))
        .then(localStorage.setItem('userToken', JSON.stringify(token.access_token)))
    }
  }, [])


  const renderPage = () => {
    if (user.token) {
      return (
        <h1 className="App__welcome">Welcome to my app, {user && user.name}</h1>
      )
    } else {
      return (
        <>
        <h2 className="App__prompt">Welcome! Please log in to your Spotify Account.</h2>
        <a href="http://localhost:8888/login"><button className="Auth__button">Log In</button></a>
        </>
      )
    }
  }

  return (
  <Layout>
    <div>
      <>
      {renderPage()}
      </>
    </div>
  </Layout>
  )
}

export default App;
